import React, { useEffect } from 'react';
import Navbar from './components/Navbar';
import AboutMe from './components/AboutMe';
import Qualifications from './components/Qualifications';
import Journey from './components/Journey';
import Projects from './components/Projects';
import Hobbies from './components/Hobbies';
import './styles.css';

function App() {
  useEffect(() => {
    // Function to add 'active' class to the corresponding nav link
    const setActiveNavLink = (index) => {
      const navLinks = document.querySelectorAll('.nav-link');
      navLinks.forEach(navLink => navLink.classList.remove('active'));
      if (navLinks[index]) {
        navLinks[index].classList.add('active');
      }
    };

    // Function to determine which section is in view
    const findActiveSection = () => {
      const sections = document.querySelectorAll('section');
      let activeSectionIndex = 0;

      sections.forEach((section, idx) => {
        const sectionTop = section.offsetTop - 50; // Adjust 50 as needed for offset
        const sectionBottom = sectionTop + section.offsetHeight;

        if (window.scrollY >= sectionTop && window.scrollY < sectionBottom) {
          activeSectionIndex = idx;
        }
      });

      return activeSectionIndex;
    };

    // Initial setting of active nav link
    setActiveNavLink(findActiveSection());

    // Event listener for scroll to update active nav link
    const handleScroll = () => {
      const activeSectionIndex = findActiveSection();
      setActiveNavLink(activeSectionIndex);
    };

    window.addEventListener('scroll', handleScroll);

    // Clean up the event listener
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []); // Only run this effect on mount and unmount

  return (
    <div>
      <Navbar />
      <section id="aboutMe">
        <AboutMe />
      </section>
      <section id="qualifications">
        <Qualifications />
      </section>
      <section id="journey">
        <Journey />
      </section>
      <section id="projects">
        <Projects />
      </section>
      <section id="hobbies">
        <Hobbies />
      </section>
    </div>
  );
}

export default App;
