import React, { useState, useEffect } from 'react';
import './Navbar.css';

const Navbar = () => {
  const [navbarVisible, setNavbarVisible] = useState(false);

  useEffect(() => {
    const handleMouseMove = (e) => {
      const navbar = document.querySelector('.navbar');
      if (navbar && navbar.contains(e.target)) {
        setNavbarVisible(true);
      } else {
        setNavbarVisible(false);
      }
    };

    window.addEventListener('mousemove', handleMouseMove);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  return (
    <nav className={`navbar ${navbarVisible ? 'visible' : 'hidden'}`}>
      <ul>
        <li><a href="#aboutme" className="nav-link">About Me</a></li>
        <li><a href="#qualifications" className="nav-link">My Qualifications</a></li>
        <li><a href="#journey" className="nav-link">My Journey</a></li>
        <li><a href="#projects" className="nav-link">Projects</a></li>
        <li><a href="#hobbies" className="nav-link">Hobbies</a></li>
      </ul>
    </nav>
  );
};

export default Navbar;
