import React, { useState, useEffect } from 'react';
import { Link } from 'react-scroll';
import './Navbar.css';

const Navbar = () => {
  const [showNavbar, setShowNavbar] = useState(true);

  useEffect(() => {
    const handleScroll = () => {
      const top = window.scrollY;
      if (top > 50) {
        setShowNavbar(false);
      } else {
        setShowNavbar(true);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <nav className={showNavbar ? 'navbar-show' : 'navbar-hide'}>
      <ul>
        <li><Link to="aboutMe" smooth={true} duration={500}>About Me</Link></li>
        <li><Link to="qualifications" smooth={true} duration={500}>My Qualifications</Link></li>
        <li><Link to="journey" smooth={true} duration={500}>My Journey</Link></li>
        <li><Link to="projects" smooth={true} duration={500}>Projects</Link></li>
        <li><Link to="hobbies" smooth={true} duration={500}>Hobbies</Link></li>
      </ul>
    </nav>
  );
};

export default Navbar;
