import React from 'react';
import Navbar from './components/Navbar';
import AboutMe from './components/AboutMe'; // Ensure casing matches the file name
import Qualifications from './components/Qualifications';
import Journey from './components/Journey';
import Projects from './components/Projects';
import Hobbies from './components/Hobbies';
import './styles.css'; // Update to the correct CSS file name

const App = () => {
  const [navbarVisible, setNavbarVisible] = useState(true);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY < 50) {
        setNavbarVisible(true);
      } else {
        setNavbarVisible(false);
      }
    };

    const handleMouseMove = (e) => {
      if (e.clientY < 100) {
        setNavbarVisible(true);
      } else {
        setNavbarVisible(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    window.addEventListener('mousemove', handleMouseMove);

    return () => {
      window.removeEventListener('scroll', handleScroll);
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  useEffect(() => {
    const sections = document.querySelectorAll('section');
    const navLinks = document.querySelectorAll('.nav-link');

    const handleScroll = () => {
      sections.forEach((section, idx) => {
        const sectionTop = section.offsetTop - 50; // Adjust 50 as needed for offset
        const sectionBottom = sectionTop + section.offsetHeight;

        if (window.scrollY >= sectionTop && window.scrollY < sectionBottom) {
          navLinks.forEach(navLink => {
            navLink.classList.remove('active');
          });
          navLinks[idx].classList.add('active');
        }
      });
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);
  return (
    <div>
      <Navbar />
      <section id="aboutMe" className="section">
        <AboutMe />
      </section>
      <section id="qualifications" className="section">
        <Qualifications />
      </section>
      <section id="journey" className="section">
        <Journey />
      </section>
      <section id="projects" className="section">
        <Projects />
      </section>
      <section id="hobbies" className="section">
        <Hobbies />
      </section>
    </div>
  );
}

export default App;
