import React, { useState, useEffect } from 'react';
import { Link } from 'react-scroll';
import './Navbar.css';

const Navbar = () => {
  const [navbarVisible, setNavbarVisible] = useState(true);
  const [lastScrollTop, setLastScrollTop] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollTop = window.pageYOffset || document.documentElement.scrollTop;

      if (currentScrollTop > lastScrollTop) {
        // Scroll down
        setNavbarVisible(false);
      } else {
        // Scroll up or at the top
        setNavbarVisible(true);
      }
      setLastScrollTop(currentScrollTop <= 0 ? 0 : currentScrollTop);
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [lastScrollTop]);

  const handleMouseEnter = () => {
    setNavbarVisible(true);
  };

  const handleMouseLeave = () => {
    if (window.scrollY > 50) {
      setNavbarVisible(false);
    }
  };

  return (
    <nav className={`navbar ${navbarVisible ? 'visible' : 'hidden'}`}
         onMouseEnter={handleMouseEnter}
         onMouseLeave={handleMouseLeave}>
      <ul>
        <li><Link to="aboutMe" smooth={true} duration={500}>About Me</Link></li>
        <li><Link to="qualifications" smooth={true} duration={500}>My Qualifications</Link></li>
        <li><Link to="journey" smooth={true} duration={500}>My Journey</Link></li>
        <li><Link to="projects" smooth={true} duration={500}>Projects</Link></li>
        <li><Link to="hobbies" smooth={true} duration={500}>Hobbies</Link></li>
      </ul>
    </nav>
  );
};

export default Navbar;
