import React, { useState } from 'react';
import { Link, Events, animateScroll as scroll, scrollSpy, scroller } from 'react-scroll';
import './Navbar.css';

const Navbar = () => {
  const [activeSection, setActiveSection] = useState('');

  const handleSetActive = (to) => {
    setActiveSection(to);
  };

  // Add event listener for scrolling
  React.useEffect(() => {
    Events.scrollEvent.register('begin', function () {
      console.log('begin', arguments);
    });

    Events.scrollEvent.register('end', function () {
      console.log('end', arguments);
    });

    scrollSpy.update();

    return () => {
      Events.scrollEvent.remove('begin');
      Events.scrollEvent.remove('end');
    };
  }, []);

  return (
    <nav>
      <ul>
        <li>
          <Link
            activeClass="active"
            to="aboutMe"
            spy={true}
            smooth={true}
            duration={500}
            onSetActive={handleSetActive}
          >
            About Me
          </Link>
          {activeSection === 'aboutMe' && <div className="highlight-box"></div>}
        </li>
        <li>
          <Link
            activeClass="active"
            to="qualifications"
            spy={true}
            smooth={true}
            duration={500}
            onSetActive={handleSetActive}
          >
            My Qualifications
          </Link>
          {activeSection === 'qualifications' && <div className="highlight-box"></div>}
        </li>
        <li>
          <Link
            activeClass="active"
            to="journey"
            spy={true}
            smooth={true}
            duration={500}
            onSetActive={handleSetActive}
          >
            My Journey
          </Link>
          {activeSection === 'journey' && <div className="highlight-box"></div>}
        </li>
        <li>
          <Link
            activeClass="active"
            to="projects"
            spy={true}
            smooth={true}
            duration={500}
            onSetActive={handleSetActive}
          >
            Projects
          </Link>
          {activeSection === 'projects' && <div className="highlight-box"></div>}
        </li>
        <li>
          <Link
            activeClass="active"
            to="hobbies"
            spy={true}
            smooth={true}
            duration={500}
            onSetActive={handleSetActive}
          >
            Hobbies
          </Link>
          {activeSection === 'hobbies' && <div className="highlight-box"></div>}
        </li>
      </ul>
    </nav>
  );
};

export default Navbar;
