import React, { useState, useEffect, useRef } from 'react';
import './Navbar.css';

const Navbar = () => {
  const [navbarVisible, setNavbarVisible] = useState(true);
  const navbarRef = useRef(null);

  useEffect(() => {
    let lastScrollTop = 0;
    const handleScroll = () => {
      let scrollTop = window.pageYOffset || document.documentElement.scrollTop;
      if (scrollTop > lastScrollTop) {
        // Downscroll
        setNavbarVisible(false);
      } else {
        // Upscroll
        setNavbarVisible(true);
      }
      lastScrollTop = scrollTop <= 0 ? 0 : scrollTop;
    };

    const handleMouseMove = (e) => {
      if (navbarRef.current && !navbarRef.current.contains(e.target)) {
        setNavbarVisible(false);
      } else if (e.clientY < 100) {
        setNavbarVisible(true);
      }
    };

    window.addEventListener('scroll', handleScroll);
    window.addEventListener('mousemove', handleMouseMove);

    return () => {
      window.removeEventListener('scroll', handleScroll);
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  return (
    <nav className={`navbar ${navbarVisible ? 'visible' : 'hidden'}`} ref={navbarRef}>
      <ul>
        <li><a href="#aboutme" className="nav-link">About Me</a></li>
        <li><a href="#qualifications" className="nav-link">My Qualifications</a></li>
        <li><a href="#journey" className="nav-link">My Journey</a></li>
        <li><a href="#projects" className="nav-link">Projects</a></li>
        <li><a href="#hobbies" className="nav-link">Hobbies</a></li>
      </ul>
    </nav>
  );
};

export default Navbar;
